import "bootstrap/dist/css/bootstrap.min.css";
import "./registration.css";

const Register = () =>{
    const[formData, setFormData] =useState({name:"", email:"", password:"", confirm_password:""});

    const handleChange = (e) =>{
        const {name, value} = e.target;
        setFormData({...formData, [name]: value})
    }

    const handleSubmit = (e) =>{
        e.preventDefault();
        console.log("Registered successfully", formData);   
    }

    return(
        <div className="Registration-card">
            <div>
                <h1>Register yourself here</h1>
            <div>
            <input className="f" placeholder="Enter your name "></input>
            </div>
            <div>
            <input placeholder="Enter your email"></input>
            </div>
            <div>
                <input placeholder="Enter the password"></input>
            </div>
            <div>
                <input placeholder="Enter the confirm password"></input>
            </div>
        </div>
        </div>
    )
}
export default Register;
