import React, {useState} from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./registration.css";

const Register = () =>{
    const[formData, setFormData] =useState({name:"", email:"", password:"", confirm_password:""});

    const handleChange = (e) =>{
        const {name, value} = e.target;
        setFormData({...formData, [name]: value})
    }

    const handleSubmit = (e) =>{
        e.preventDefault();
        if(formData.password !== formData.confirm_password){
            alert("Passwords do not match!!");
            return;
        }
        console.log("Registered successfully", formData);   
        setFormData({name:"",email:"", password})
    }

    return(
        <div className="registration-container">
            <div className="registration-card">
            <div>
                <h1 className="register-card-title">Register yourself here</h1>
                <form onSubmit={handleSubmit}>
            <div className="mb-3">
            <input type="name" id="name" name="name" value={formData.name} onChange={handleChange} className="form-control" placeholder="Enter your name " required></input>
            </div>
            <div className="mb-3">
            <input type="email" id="email" name="email" value={formData.email} onChange={handleChange} className="form-control" placeholder="Enter your email " required></input>
            </div>
            <div className="mb-3">
                <input type="password" id="password" name="password" value={formData.password} onChange={handleChange} className="form-control" placeholder="Enter the password " required></input>
            </div>
            <div className="mb-3">
                <input type="password" id="confirm_password" name="confirm_password" value={formData.confirm_password} onChange={handleChange} className="form-control" placeholder="Enter the confirm password " required></input>
            </div>
            <div className="d-gridmt-3">
            <button className="btn btn-primary" type="submit">Register</button>
            </div>
            </form>
        </div>
        </div>
        </div>
    )
}
export default Register;
