import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom"; // To read the passed state
import "bootstrap/dist/css/bootstrap.min.css";
import "./login.css";

const Login = () => {
    const location = useLocation(); // Get the location object
    const successMessage = location.state?.successMessage; // Extract success message from state
    const [message, setMessage] = useState("");

    useEffect(() => {
        // Delay the display of the success message by 2 seconds
        if (successMessage) {
            setTimeout(() => {
                setMessage(successMessage);
            }, 2000); // 2-second delay
        }
    }, [successMessage]); // Trigger the effect only when the message is available

    return (
        <div className="login-container">
            <div className="login-card">
                <h1 className="login-card-title">Login</h1>
                {message && (
                    <div className="alert alert-success" role="alert">
                        {message}
                    </div>
                )}
                <form>
                    <div className="mb-3">
                        <input
                            type="email"
                            className="form-control"
                            placeholder="Enter your email"
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Enter your password"
                            required
                        />
                    </div>
                    <div className="d-grid mt-3">
                        <button className="btn btn-primary" type="submit" href="#home">
                            Login
                        </button>
                    </div>
                </form>
                <p className="text-center mt-3">
                    Don't have an account?{" "}
                    <a href="/restrat">Register here</a>
                </p>
            </div>
        </div>
    );
};

export default Login;
