import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./registration.css";

const Register = () => {
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        password: "",
        confirm_password: "",
    });

    const [error, setError] = useState(null); // State for handling errors

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Password validation
        if (formData.password !== formData.confirm_password) {
            alert("Passwords do not match!");
            return;
        }

        try {
            // Send data to backend
            const response = await fetch("http://localhost:5000/register", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                const data = await response.json();
                console.log(data.message); // Assuming the backend sends a message
                alert("Registration successful!"); // Optional success alert
            } else {
                const errorData = await response.json();
                setError(errorData.error || "Error registering user"); // Show error if any
                console.error("Error registering user:", errorData);
            }
        } catch (error) {
            console.error("Error:", error);
            setError("An unexpected error occurred"); // Show unexpected errors
        }

        // Clear the form after submission
        setFormData({ name: "", email: "", password: "", confirm_password: "" });
    };

    return (
        <div className="registration-container">
            <div className="registration-card">
                <h1 className="register-card-title">Register yourself here</h1>
                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <input
                            type="text"
                            id="name"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            className="form-control"
                            placeholder="Enter your name"
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            className="form-control"
                            placeholder="Enter your email"
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <input
                            type="password"
                            id="password"
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                            className="form-control"
                            placeholder="Enter your password"
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <input
                            type="password"
                            id="confirm_password"
                            name="confirm_password"
                            value={formData.confirm_password}
                            onChange={handleChange}
                            className="form-control"
                            placeholder="Confirm your password"
                            required
                        />
                    </div>
                    {error && <div className="alert alert-danger">{error}</div>} {/* Show error message */}
                    <div className="d-grid mt-3">
                        <button className="btn btn-primary" type="submit">
                            Register
                        </button>
                    </div>
                </form>
            </div>
            <div>
                <button>Back to Login</button>
            </div>
        </div>
    );
};

export default Register;
